INSERT INTO `wp_aioseo_notifications` VALUES (1,'61fa46f606544','競合するプラグインが検出されました','警告: AIOSEO が他の有効な SEO またはサイトマッププラグインを検出しました。競合を防ぐために、次のプラグインを無効化することをおすすめします:<ul><li><strong>Google XML Sitemaps</strong></li></ul>','error','[\"all\"]',NULL,'conflicting-plugins','2022-03-02 10:23:54',NULL,'今すぐ修正','http://action#sitemap/deactivate-conflicting-plugins?refresh','後で再通知','http://action#notification/conflicting-plugins-reminder',0,0,'2022-02-02 08:55:18','2022-02-23 10:24:58');
INSERT INTO `wp_aioseo_notifications` VALUES (2,'61fa46f620b3f','📢 Introducing Link Assistant + Our Stand-Alone Headline Analyzer','Check out the brand new features in AIOSEO 4.1.6! First, we have Link Assistant, a powerful tool to help you build effective internal links and audit external ones.\r\n<br><br>\r\nWe’ve also added a stand-alone Headline Analyzer to help you craft, and A/B test your headlines right from within WordPress.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.6 today and up your SEO game without breaking a sweat!','success','[\"4-x\",\"lite\"]',203,NULL,'2022-02-08 13:39:00',NULL,'Learn More','https://aioseo.com/new-introducing-link-assistant-a-stand-alone-headline-analyzer-and-more-in-aioseo-4-1-6/?utm_source=WordPress&utm_campaign=link-assistant-v4-lite&utm_medium=plugin-notification&utm_content=Learn More','Get AIOSEO Pro','https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=link-assistant-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro',1,0,'2022-02-02 08:55:18','2022-02-23 10:19:53');
INSERT INTO `wp_aioseo_notifications` VALUES (3,'62160a49c3406','🎉 Introducing IndexNow and Page Builder Integrations','Check out the fantastic new features in AIOSEO 4.1.7! First, we have IndexNow, a feature that gets your new or updated content indexed faster on Bing and Yandex (with more search engines to follow).\r\n<br><br>\r\nWe’ve also integrated AIOSEO with 3 major page builders, namely Elementor, Divi and SeedProd. Now you can work on your SEO right from within your page builder.\r\n<br><br>\r\nGet AIOSEO Pro today and up your SEO game without breaking a sweat!','success','[\"4-x\",\"lite\"]',223,NULL,'2022-02-08 13:39:00',NULL,'Learn More','https://aioseo.com/introducing-indexnow-and-page-builder-integrations-in-aioseo-4-1-7/?utm_source=WordPress&utm_campaign=index-now-v4-lite&utm_medium=plugin-notification&utm_content=Learn More','Get AIOSEO Pro','https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=index-now-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro',1,0,'2022-02-23 10:19:53','2022-02-23 10:23:57');
