INSERT INTO `wp_aioseo_posts` VALUES (3,9,'','Taro Ejiriへの問い合わせページ。仕事のご依頼については、翌日までにご返答いたします。基本活動日はカレンダー通りです。',NULL,NULL,NULL,NULL,NULL,NULL,'default','default',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0,'default','default',NULL,NULL,NULL,NULL,NULL,0,'default',NULL,0,1,0,0,0,0,0,0,0,NULL,NULL,'large',NULL,NULL,'2022-02-02 08:55:55',NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-02 08:55:55','2022-02-02 08:55:55');
INSERT INTO `wp_aioseo_posts` VALUES (4,37,NULL,'Taro Ejiriの略歴について。略歴、コーディング時のスタンスなどについて記載してます。',NULL,NULL,NULL,NULL,NULL,NULL,'default','default',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0,'default','default',NULL,NULL,NULL,NULL,NULL,0,'default',NULL,0,1,0,0,0,0,0,0,0,NULL,NULL,'large',NULL,NULL,'2022-02-02 08:55:55',NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-02 08:55:55','2022-02-02 08:55:55');
