INSERT INTO `wp_postmeta` VALUES (501,121,'_form','<label> お名前 <span class=\"asterisk\">(必須)</span></label>\n<p>[text* your-name id:contact_name placeholder \"例：○○事務所、△△株式会社\"]</p>\n\n<label> メールアドレス <span class=\"asterisk\">(必須)</span> </label>\n<p>[email* your-email id:contact_email placeholder \"例：sample@sample.com\"]</p>\n\n<label> お電話番号</label>\n<p>[tel your-tel id:contact_tel placeholder \"例：03-1234-5678\"]</p>\n\n<label> ご件名</label>\n<p>[select menu-884 \"選択してください\" \"就業について\" \"制作のご依頼\" \"その他\"]</p>\n\n<label> お問い合わせ内容</label>\n<p>[textarea your-message id:contact_message]</p>\n\n<div class=\"submitFrame\">\n<div id=\"submit\">[submit \"送信する\"]</div>\n</div>');
INSERT INTO `wp_postmeta` VALUES (502,121,'_mail','a:9:{s:6:\"active\";b:1;s:7:\"subject\";s:37:\"ET ウェブデザイン \"[menu-884]\"\";s:6:\"sender\";s:39:\"[your-name] <wordpress@portfolio.local>\";s:9:\"recipient\";s:18:\"sept1976@gmail.com\";s:4:\"body\";s:457:\"差出人: [your-name] \nメールアドレス: <[your-email]>\nお電話番号: [your-tel]\nご件名: [menu-884]\nメッセージ本文:\n[your-message]\n\n--------------------------------------\nIPアドレス：[_remote_ip]\nページタイトル：[_post_title]\n送信日時：[_date][_time]\n--------------------------------------\nこのメールは ET ウェブデザイン (http://portfolio.local/) のお問い合わせフォームから送信されました\";s:18:\"additional_headers\";s:22:\"Reply-To: [your-email]\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:0;s:13:\"exclude_blank\";b:0;}');
INSERT INTO `wp_postmeta` VALUES (503,121,'_mail_2','a:9:{s:6:\"active\";b:1;s:7:\"subject\";s:88:\"ET ウェブデザイン にお問い合わせ頂きありがとうございました。\";s:6:\"sender\";s:51:\"ET ウェブデザイン<wordpress@portfolio.local>\";s:9:\"recipient\";s:12:\"[your-email]\";s:4:\"body\";s:666:\"[your-name]様　\n\nこの度はお問い合せ頂き誠にありがとうございました。\n改めてご連絡をさせていただきます。\n\n--- ご送信内容の確認 ---\nお名前：　[your-name]\nメールアドレス：　[your-email]\nお電話番号：　[your-tel]\n\n--- お問い合わせ内容 ---\nご件名: [menu-884]\n[your-message]\n\n--------------------------------------\nこのメールに心当たりの無い場合は、お手数ですが\n下記連絡先までお問い合わせください。\nこの度はお問い合わせ重ねてお礼申し上げます。\n--------------------------------------\n\n江尻 大郎\nE-mail: sept1976@gmail.com　\";s:18:\"additional_headers\";s:28:\"Reply-To: sept1976@gmail.com\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:0;s:13:\"exclude_blank\";b:0;}');
INSERT INTO `wp_postmeta` VALUES (504,121,'_messages','a:22:{s:12:\"mail_sent_ok\";s:75:\"ありがとうございます。メッセージは送信されました。\";s:12:\"mail_sent_ng\";s:84:\"メッセージの送信に失敗しました。後でまたお試しください。\";s:16:\"validation_error\";s:81:\"入力内容に問題があります。確認して再度お試しください。\";s:4:\"spam\";s:84:\"メッセージの送信に失敗しました。後でまたお試しください。\";s:12:\"accept_terms\";s:66:\"メッセージを送信する前に承諾確認が必要です。\";s:16:\"invalid_required\";s:42:\"必須項目に入力してください。\";s:16:\"invalid_too_long\";s:48:\"入力されたテキストが長すぎます。\";s:17:\"invalid_too_short\";s:48:\"入力されたテキストが短すぎます。\";s:13:\"upload_failed\";s:81:\"ファイルのアップロード時に不明なエラーが発生しました。\";s:24:\"upload_file_type_invalid\";s:66:\"この形式のファイルはアップロードできません。\";s:21:\"upload_file_too_large\";s:36:\"ファイルが大きすぎます。\";s:23:\"upload_failed_php_error\";s:72:\"ファイルのアップロード中にエラーが発生しました。\";s:12:\"invalid_date\";s:45:\"日付の形式が正しくありません。\";s:14:\"date_too_early\";s:42:\"選択された日付は早すぎます。\";s:13:\"date_too_late\";s:42:\"選択された日付は遅すぎます。\";s:14:\"invalid_number\";s:45:\"数値の形式に間違いがあります。\";s:16:\"number_too_small\";s:45:\"入力された数値が小さすぎます。\";s:16:\"number_too_large\";s:48:\"数値が最大許容値を超えています。\";s:23:\"quiz_answer_not_correct\";s:48:\"クイズの答えが正しくありません。\";s:13:\"invalid_email\";s:66:\"入力されたメールアドレスに間違いがあります。\";s:11:\"invalid_url\";s:34:\"URL に間違いがあります。\";s:11:\"invalid_tel\";s:42:\"電話番号に間違いがあります。\";}');
