INSERT INTO `wp_posts` VALUES (9,1,'2018-01-06 16:51:24','2018-01-06 07:51:24','<article>\r\n  <div class=\"container\">\r\n    <section id=\"contact\">\r\n      <h2 class=\"top\">Contact</h2>\r\n      <div class=\"frame\">\r\n        <p>以下のフォームに必要事項をご記入の上、「メールを送信」ボタンを押してください。<br>正常に送信された場合、ご質問内容を記述した確認メールを自動返信いたします。</p>\r\n        <fieldset>\r\n        <legend class=\"form_heading\" accesskey=\"a\">情報の入力</legend>\r\n          [contact-form-7 id=\"121\" title=\"お問い合わせフォーム\"]\r\n        </fieldset>\r\n        <div class=\"border-wrap-small\">\r\n          <ul>\r\n            <li>このメールフォームはテスト用ではなく、実際に私宛てにメールが届きます。予めご了承下さい。</li>\r\n            <li>お仕事のご依頼については、翌日までにご返答いたします。基本活動日はカレンダー通りです。</li>\r\n            <li>もし返信がない場合は「迷惑メール」フォルダもご確認ください。</li>\r\n          </ul>\r\n        </div>\r\n      </div>\r\n    </section>\r\n  </div>\r\n</article>','お問い合わせ','','publish','closed','closed','','contact','','','2018-06-11 02:39:04','2018-06-10 17:39:04','',0,'http://portfolio.local/?page_id=9',2,'page','',0);
