INSERT INTO `wp_posts` VALUES (149,1,'2018-01-18 16:55:01','2018-01-18 07:55:01','<article>\r\n  <div class=\"container\">\r\n    <section id=\"thanks\">\r\n      <h2>お問い合わせありがとうございます。<br />ご記入いただいたメールアドレスに、ご確認メールを送信しました。</h2>\r\n      <div class=\"thanks_text\">\r\n        <p>お問い合わせ内容を確認しまして、折り返しご連絡致します。</p>\r\n        <p>※ドメイン指定受信やドメイン指定受信拒否等を設定している場合、当サイトからの返信メールが届かない事がございます。</p>\r\n        <p>あらかじめ設定を解除、あるいはドメイン指定設定を行いメールが受信できるように設定して下さい。</p>\r\n      </div>\r\n      <div class=\"topReturn\">\r\n        <a href=\"/\">トップページに戻る</a>\r\n      </div>\r\n    </section>\r\n  </div>\r\n</article>','送信完了','','publish','closed','closed','','thanks','','','2018-06-11 09:23:48','2018-06-11 00:23:48','',0,'http://portfolio.local/?page_id=149',3,'page','',0);
