INSERT INTO `wp_posts` VALUES (247,1,'2018-06-11 02:42:48','2018-06-10 17:42:48','\r\n\r\n<article>\r\n<div class=\"container\">\r\n\r\n<section id=\"thanks\">\r\n<h2>お問い合わせありがとうございます。<br />ご記入いただいたメールアドレスに、ご確認メールを送信しました。</h2>\r\n<div class=\"thanks_text\">\r\n<p>お問い合わせ内容を確認しまして、折り返しご連絡致します。</p>\r\n<p>※ドメイン指定受信やドメイン指定受信拒否等を設定している場合、当サイトからの返信メールが届かない事がございます。</p>\r\n<p>あらかじめ設定を解除、あるいはドメイン指定設定を行いメールが受信できるように設定して下さい。</p>\r\n</div>\r\n<div class=\"topReturn\">\r\n<a href=\"/\">トップページに戻る</a>\r\n</div>\r\n</section>\r\n\r\n</div>\r\n</article>','送信完了','','inherit','closed','closed','','149-revision-v1','','','2018-06-11 02:42:48','2018-06-10 17:42:48','',149,'http://portfolio.local/149-revision-v1/',0,'revision','',0);
