INSERT INTO `wp_posts` VALUES (263,1,'2018-09-17 17:11:02','2018-09-17 08:11:02','a:10:{s:4:\"type\";s:8:\"textarea\";s:12:\"instructions\";s:68:\"改行はbrではなく、１行分空ければ改行されます。\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:0:\"\";s:11:\"placeholder\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:4:\"rows\";i:12;s:9:\"new_lines\";s:2:\"br\";}','概要欄','column','publish','closed','closed','','field_5a571adc79d9a','','','2018-09-17 17:11:02','2018-09-17 08:11:02','',256,'http://portfolio.local/?post_type=acf-field&p=263',11,'acf-field','',0);
INSERT INTO `wp_posts` VALUES (264,1,'2018-09-17 17:11:02','2018-09-17 08:11:02','a:12:{s:4:\"type\";s:8:\"checkbox\";s:12:\"instructions\";s:60:\"サイト制作の際に関わった作業内容について\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:7:\"choices\";a:5:{s:11:\"SITE DESIGN\";s:11:\"SITE DESIGN\";s:6:\"CODING\";s:6:\"CODING\";s:10:\"RESPONSIVE\";s:10:\"RESPONSIVE\";s:3:\"SEO\";s:3:\"SEO\";s:16:\"GOOGLE ANALYTICS\";s:16:\"GOOGLE ANALYTICS\";}s:13:\"default_value\";a:0:{}s:6:\"layout\";s:8:\"vertical\";s:12:\"allow_custom\";i:0;s:11:\"save_custom\";i:0;s:6:\"toggle\";i:0;s:13:\"return_format\";s:5:\"value\";}','作業範囲','scope','publish','closed','closed','','field_5a571b6747240','','','2018-09-17 17:11:02','2018-09-17 08:11:02','',256,'http://portfolio.local/?post_type=acf-field&p=264',12,'acf-field','',0);
INSERT INTO `wp_posts` VALUES (265,1,'2018-09-17 17:11:02','2018-09-17 08:11:02','a:11:{s:4:\"type\";s:4:\"text\";s:12:\"instructions\";s:36:\"クライアント名（日本語）\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:0:\"\";s:11:\"placeholder\";s:0:\"\";s:7:\"prepend\";s:0:\"\";s:6:\"append\";s:0:\"\";s:10:\"formatting\";s:4:\"html\";s:9:\"maxlength\";s:0:\"\";}','クライアント名（日本語）','client_jpn','publish','closed','closed','','field_5a571c1074af5','','','2018-09-17 17:11:02','2018-09-17 08:11:02','',256,'http://portfolio.local/?post_type=acf-field&p=265',5,'acf-field','',0);
